package com.example.testapp.entity;import jakarta.persistence.*;@Entitypublic class Image {    @Id    @GeneratedValue(strategy = GenerationType.IDENTITY)    private int id;    private String name;    @Lob    private byte[] data;    private String username;    public Image() {    }    public Image(byte[] data, String name, String username) {        this.data = data;        this.name = name;        this.username = username;    }    public int getId() {        return id;    }    public void setId(int id) {        this.id = id;    }    public String getName() {        return name;    }    public String getUsername() {        return username;    }    public void setName(String name) {        this.name = name;    }    public byte[] getData() {        return data;    }    public void setData(byte[] data) {        this.data = data;    }    public void setUsername(String username) {        this.username = username;    }}